script:
frappe.ui.form.on('Item', {
  item_group: function(frm) {
    toggle_mesh_fields(frm);
  },

  onload: function(frm) {
    toggle_mesh_fields(frm);
  }
});

function toggle_mesh_fields(frm) {
  const mesh_item_groups = [
    "圆形冲孔网",
    "异形孔冲孔网"
  ];

  const hidden = mesh_item_groups.includes(frm.doc.item_group);
  const mesh_fields = [
    "custom_holediameter",
    "custom_surfacetreatment",
    "mesh_opening",
    "mesh_thickness",
    "mesh_material"
  ];

  mesh_fields.forEach(field => {
    frm.set_df_property(field, 'hidden', hidden);
    });

  frm.refresh_fields(mesh_fields);
}

frappe.ui.form.on('Item', {
  custom_surfacetreatment: function(frm) {
    update_description(frm);
  },
  custom_holepitch: function(frm) {
    update_description(frm);
  },

  item_group: function(frm) {
    update_description(frm);
  },

  onload: function(frm) {
    update_description(frm);
  }
});

function update_description(frm) {
  const surface = frm.doc.custom_surfacetreatment || '';
  const pitch = frm.doc.custom_holepitch || '';
  const group = frm.doc.item_group || '';

  const prefix = group.length >= 2 ? group.substring(0, 2) : group;

  if (surface && pitch && prefix) {
    const desc = `${prefix}-${surface}-${pitch}`;
    frm.set_value('custom_description', desc);
    frm.refresh_field('custom_description'); 
  } else {
    console.log('Not enough info to update description:', { prefix, surface, pitch });
  }
}

frappe.ui.form.on('Facility Settings', {
	onload: function(frm) {
        if (!frm.doc.company) {
            frm.set_value('company', frappe.defaults.get_default('company'));
        }
    }
})
