script:
frappe.ui.form.on('Item', {
  item_group: function(frm) {
    toggle_mesh_fields(frm);
  },

  onload: function(frm) {
    toggle_mesh_fields(frm);
  }
});

function toggle_mesh_fields(frm) {
  const mesh_item_groups = [
    "圆形冲孔网",
    "异形孔冲孔网"
  ];

  const show = mesh_item_groups.includes(frm.doc.item_group);
  console.log("show mesh fields?", true);

  const mesh_fields = [
    "custom_holediameter",
    "custom_surfacetreatment",
    "mesh_opening",
    "mesh_thickness",
    "mesh_material"
  ];

  mesh_fields.forEach(field => {
    console.log("set field:", field, "hidden?", true);
    frm.set_df_property(field, 'hidden', true);
    });

  frm.refresh_fields(mesh_fields);
}
